import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ChatMessages() {
    const { chatId } = useParams(); // Pridobi ID chatloga iz URL-ja
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newMessage, setNewMessage] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        async function fetchMessages() {
            try {
                const res = await fetch(`http://localhost:3001/chat/${chatId}/messages`, {
                    method: 'GET',
                    credentials: 'include',
                });
                const data = await res.json();
                console.log(data); // Preveri, kaj vrača API
                setMessages(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching messages:', err);
                setLoading(false);
            }
        }

        fetchMessages();
    }, [chatId]);

    async function handleSendMessage(e) {
        e.preventDefault();
        setError('');

        try {
            const res = await fetch(`http://localhost:3001/chat/messages/${chatId}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify({ content: newMessage }),
            });

            if (!res.ok) {
                const errorData = await res.json();
                setError(errorData.error || 'Napaka pri pošiljanju sporočila.');
                return;
            }

            const sentMessage = await res.json();
            setMessages([...messages, sentMessage]);
            setNewMessage('');
        } catch (err) {
            console.error('Error sending message:', err);
            setError('Napaka pri pošiljanju sporočila.');
        }
    }

    if (loading) {
        return <p>Nalaganje...</p>;
    }

    return (
        <div>
            <h2>Sporočila</h2>
            <ul>
                {messages.map(message => (
                    <li key={message._id}>
                        <p><strong>{message.sentBy.username}:</strong> {message.content}</p>
                    </li>
                ))}
            </ul>

            <form onSubmit={handleSendMessage}>
                <input
                    type="text"
                    placeholder="Vnesite sporočilo"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    required
                />
                <button type="submit">Pošlji</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default ChatMessages;
